import React, { useState } from 'react'


export const AddCampistaComponent = () => {

    const [nombre, setNombre] = useState('');
    const [apellido, setApellido] = useState('');
    const [email, setEmail] = useState('');

    const saveCampista = (e) => {
        e.preventDefault();
        const campista = { nombre, apellido, email };
        console.log(campista);
    }

  return (
    <div>
        <div className='container'>
            <div className='row'>
            <div className='card col-md-6 offset-md-3 offset-md-3'>
                <h2 className='text-center'>Registro de campistas</h2>
                <div className='card-body'>
                    <form>
                        <div className='form-group mb-2'>
                            <label className='form-label'>Nombre</label>
                            <input
                                type='text'
                                placeholder='Digite su nombre'
                                name='nombre'
                                className='form-control'
                                value={ nombre }
                                onChange={ (e) => setNombre(e.target.value) }
                            />
                        </div>
                        <div className='form-group mb-2'>
                            <label className='form-label'>Apellido</label>
                            <input
                                type='text'
                                placeholder='Digite su apellido'
                                name='apellido'
                                className='form-control'
                                value={ apellido }
                                onChange={ (e) => setApellido(e.target.value) }
                            />
                        </div>
                        <div className='form-group mb-2'>
                            <label className='form-label'>Email</label>
                            <input
                                type='email'
                                placeholder='Digite su email'
                                name='email'
                                className='form-control'
                                value={ email }
                                onChange={ (e) => setEmail(e.target.value) }
                            />
                        </div>
                        <button className='btn btn-success' onClick={ (e) => saveCampista(e) }>Guardar</button>
                    </form>
                </div>
            </div>
            </div>
        </div>
    </div>
  )
}

export default AddCampistaComponent;
